import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';  // Import HttpHeaders here
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class ImdbService {
  private apiUrl = 'https://imdb146.p.rapidapi.com/v1/news/';
  private headers = new HttpHeaders({
    'X-RapidAPI-Key': 'da65b45924mshae583402eaa7203p14b4c7jsnbbc9f0133ea1',
    'X-RapidAPI-Host': 'imdb146.p.rapidapi.com',
  });

  constructor(private http: HttpClient) {}

  getNewsById(id: string): Observable<any> {
    const params = new HttpParams().set('id', id);

    return this.http.get(this.apiUrl, { headers: this.headers, params });
  }
}
