import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-searchmovies',
  templateUrl: './searchmovies.component.html',
  styleUrls: ['./searchmovies.component.css']
})
export class SearchmoviesComponent implements OnInit{
  query!: string;
  constructor(private activatedRoute: ActivatedRoute) {
    this.query = '';  the query property
  }

  ngOnInit(): void {
    // Subscribe to changes in the route parameters
    this.activatedRoute.paramMap.subscribe(params => {
      // Retrieve the value of the 'query' parameter from the route
      this.query = params.get('query') || '';
      // Now, 'this.query' contains the value of the 'query' parameter
      console.log(this.query);
    });
  }


}
